 let orderData = {
 "title": "Free plan purchased",
 "data": event.order
        };
        Data.insert("planEvents", orderData);
    } else {
 let orderData = {
 "title": "Regular plan purchased",
 "data": event.order
        };
        Data.insert("planEvents", orderData);
    }
}



